css:
@-moz-document domain('dribbble.com') {
	/* 
		the code in here only applie to:
			-pages on the domain http://dribbble.com
		works in Chrome and FireFox
	*/

#comments-section #comments li.current-user {background:#fffcfe;}
#comments-section #comments li.current-user h2 img {margin-top:0;}
#comments-section #comments li.current-user p {margin-right:10px;}
#comments-section #comments li.current-user .likes-list {padding-right:10px;}

#comments-section #comments li.owner {background:#f9f9f9;}
#comments-section #comments li.owner h2 img {margin-top:0;}
#comments-section #comments li.owner p {margin-right:10px;}
#comments-section #comments li.owner .likes-list {padding-right:10px;}

}
