@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("courses.mtu.edu") {

body.mybody2 {
     padding-left: 2.5%;
     width: 100%;
}
a:hover {
     color:#fffa8a !important;
}
div.contextblock a.contextlink {
     color:#000000 !important;
}
div#column2 {
}
div.mycentercolumn {
}
div#courses {
}
ul.courselist{
     background-color:#181818 !important;
}

ul.courselist li h4 {
     display:none !important;
}
ul.courselist li{
     border:none !important;
}
ul.courselist li.listhead{
     background-color:#FFF040 !important;
     margin-bottom:-4px !important;
}
ul.courselist li.listhead h1{
     color:#181818 !important;
}
ul li.listhead h2{
     color:#181818 !important;
     font-weight:bold !important;
}
ul.courselist li p{
     color:#FFF25B !important;
}
ul.courselist li a:link{
     color:#FFE62B !important;
     border:none !important;
}
ul.courselist li a:visited{
     color:#FFE62B !important;
     border:none !important;
}
ul.courselist li a:active{
     color:#FFE62B !important;
     border:none !important;
}
ul.courselist li a:hover{
     color:#fffa8a !important;
     border:none !important;
}
span#bucket {
     display:none !important;
}

}
