css:
@-moz-document domain('turntable.fm'){
.avatar_laptop,.record_pile,#top-panel + div div > div img,#top-panel + div > img{visibility:hidden;}
#spotlight,.logo,.settingsHead,.share-on,.url,.faqButton,div#top-panel div#room-info-tab div.button,#songboard_artist{display:none!important;}
body,#top-panel + div,#outer,#left_speaker,#right_speaker,div#top-panel div#room-info-tab div.content,#menuh .menuItem,#top-panel .header,#top-panel .info,.input-box,#ttpUserSearch,.playlist-container .mainPane,#TFMPL,.black-right-header,.ttpUsersListHeader,.ttpBanner{background-color:#fff!important;background:#fff!important;}
#songboard{font-family:"courier new", monaco, "freemono", "droid sans mono", "lucida console", "anonymous pro", "andale mono", "bitstream vera", "dejavu sans mono", "nimbus mono l", courier, serif;color:#000;}
#menuh,.black-right-header,.black-right-header .header-text,.ttpUsersListHeader{color:#000!important;}
#songboard_artist,#songboard_title{text-shadow:none;}
#songboard_title{position:absolute;top:10px;left:10px;}
#top-panel + div,.mainPane,div#top-panel div#room-info-tab div.content{top:0px!important;}
#playlist,.mainPane{height:700;}
.songlist{height:633;}
#playlist{top:-100px;}
#TFMPL{z-index:999;-webkit-box-shadow:none;}
#menuh .menuItem .text{margin-left:0px!important;}
#room-info-tab .song.song2{background:#f0f0f0!important;}
#top-panel{z-index:100!important;}
#right-panel{z-index:101!important;}
#menuh{z-index:102!important;top:70px!important;left:0px!important;}
#songboard,#songboard_hotspot{position:absolute!important;top:0px!important;left:0px!important;z-index:20000!important;}
div.roomView div#top-panel + div >a:first-of-type{z-index:20001}
div#outer,div.chat-container,div#ttpUsersList,div#TFMPL{position:fixed!important;top:50px!important;}
}
