css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://google.com/calendar/"), url-prefix("https://google.com/calendar/"), url-prefix("http://www.google.com/calendar/"), url-prefix("https://www.google.com/calendar/") {
  #onegoogbar, #vr-header, #sropt, .applogo, #topRightNavigation, .date-nav-today, #nav, div.date-top { display:none !important; }
  #vr-nav { border-bottom:0 !important; margin:0 !important; padding:0 !important; z-index:1; position: absolute; left: 0; }
  div#mainnav { height:0 !important; }
  #calcontent.eui-t #topLeftNavigation { left:0 !important; }
  div.date-top { }
  div#mainbody { margin-left:18px !important; }
  td.st-dtitle-today {background-color:white !important; border-left:1px solid #dddddd !important; border-top:1px solid #dddddd !important; color:#333333 !important; font-weight:normal !important;}
  td.st-bg-today {background-color:white !important; border-left:1px solid #dddddd !important;}
  .st-bg-next {border-left:1px solid #dddddd !important}
  .st-dtitle-down {border-top:1px solid #dddddd !important;}
  .te, .rb-n { height:auto !important; line-height:1 !important; }
  .te-s, .rb-n { overflow: visible !important; white-space: normal !important; }
}
