css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.carview.co.jp") {

/* Non-display part */
#headerwrap,
iframe,
#sideBlock,
.addthis_toolbox,
div#mainBlock > overture,
.enqFormBox,
.relatedArticle,
div#mainBlock > h4,
.relatedNews,
.relatedLink,
#footerW
{ display: none !important; }

/* Display part */
body,
#container,
#mainBlock,
.subTitleBox,
.reportMainBox
{
    width: 100% !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    display: inline !important;
}

#contentHeader
{
    width: 100% !important;
    background: black !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}


}
