css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.dpreview.com") {

/* Non-display part */
.ad,
.chromeHeader,
.siteFooter,
.sideBar,
.comments,
#comments
{ display: none !important; }

/* Display part */
body,
.chromeMainTop,
.chromeMainMiddle,
.chromeMainBottom,
.contentWrapper,
.mainContent,
#mainContent,
.justify,
.contentTable
{
    width: auto !important;
    background: black !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}


}
