css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#scriptish-button.tbb-scripts-available .toolbarbutton-icon,
#greasemonkey-tbb.tbb-scripts-available .toolbarbutton-icon {
   background-image: none !important;
}
#scriptish-button.tbb-scripts-available > .toolbarbutton-menubutton-button,
#greasemonkey-tbb.tbb-scripts-available > .toolbarbutton-menubutton-button {
   background-image: -moz-linear-gradient(rgba(255, 255, 255, .4), rgba(255, 255, 255, .2)),
                     url("chrome://greasefire/content/gradient.png") !important;
   background-size: 100% !important;
}
#scriptish-button.tbb-scripts-available > .toolbarbutton-menubutton-button:not([disabled="true"]):not(:active):hover,
#greasemonkey-tbb.tbb-scripts-available > .toolbarbutton-menubutton-button:not([disabled="true"]):not(:active):hover {
   background-image: -moz-linear-gradient(rgba(255, 255, 255, .4), rgba(255, 255, 255, .2)),
                     -moz-linear-gradient(rgba(255, 255, 255, .2), rgba(255, 255, 255, 0)),
                     url("chrome://greasefire/content/gradient.png") !important; 
}
#scriptish-button.tbb-scripts-available > .toolbarbutton-menubutton-button:not([disabled="true"]):hover:active,
#greasemonkey-tbb.tbb-scripts-available > .toolbarbutton-menubutton-button:not([disabled="true"]):hover:active {
   background-image: -moz-linear-gradient(rgba(210, 210, 210, .4), rgba(210, 210, 210, .2)),
                     url("chrome://greasefire/content/gradient.png") !important; 
}
