css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {}

@media print{

body{margin-top:0 !important;
margin-left:0 !important;
margin-bottom:0 !important;
padding-top:0 !important;
padding-bottom:0 !important;}

td[width="143"]{display:none !important;}
td[align="right"]{display:none !important;}

body[onload="Print()"]>font[size="-1"]{display:none !important;}
body[onload="Print()"]>table>tbody>tr>td>font[size="-1"]>b{display:none !important;}
body[onload="Print()"]>table>tbody>tr>td>font[size="-1"]>div{display:none !important;}
body[onload="Print()"]>font[size="+2"]{display:none !important;}

body>table>tbody>tr>td>table>tbody>tr>td>font>div>ul>li>a>b{display:none !important;}
body>table>tbody>tr>td>table>tbody>tr>td{
padding-top:0px !important;
}

body>table>tbody>tr>td>table>tbody>tr>td>font>div>ul>li>a+br{display:none !important;}

body>table>tbody>tr>td>table>tbody>tr>td{padding:0px !important;}
}
