css:
@-moz-document domain("bikerornot.com") 

/* ===================================================
v.1.2b  Update to Remove the new Trending links section
==================================================== */

{
/* ============ body stuff ================ */
img {border: none !important;}
  
/*-- user online/offline color--*/
.b {color: #07771F !important;}

/* added for compatibility with members custom profile pages */
div[id="headerdiv"]{background-color: transparent !important;}

/*---- hide the boring stuff ------ */
div[class="trending-hashtag-table"],
div[id="bn-foot"]{display: none!important;}
  
/* new blog style cleanup & ugly status box */
div [class="link-media"],div [class="feed-item"]{border: none!important;}
div [class="status-wrapper"]{background-color: transparent!important; border: none!important;}
input[type="submit"]{background-color: transparent!important; color: #000000!important;}

/* block the admin ads */
div [class="feed-item feed-ad"]{display: none!important;}

/* ================ block those annoying ads ===============================*/
.ad_box{display: none !important;}
div [id="googMultiAdvert"]{border: none !important;}
iframe {display: none!important;}

/* ------- Slide out box ad in lower right corner --------- */
a[id="ad_panel"] {display: none !important;}
img[id="ad_image"] {display: none !important;}
span[id="ad_bg"] {display: none !important;}

/* ------- the big google ads... top, right & bottom -------- */
div[id="google_ads_div_bon_top_fold_728_90_ad_container"] {display: none!important;}
div[id="google_ads_div_BON_left_160_600_ad_container"] {display: none!important;}
div[id="google_ads_div_Bottom_Page_ad_container"] {display: none!important;} 
div [class="ad ad728x90"]{display: none!important;} 
div[id="bon_topAd"]{display: none!important;} 

/* ----- blocks bikes for sale ads at the top - if you want these comment or remove the next line ------- */
div[id="cxAd"]{display: none!important;}

/*------ End ----*/
}
