css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soundcloud.com"),
domain("sndcdn.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

button, #header, select, body#tracks.new .overall-track-info .single-entry, .options, #main-wrapper, #main-wrapper-inner, .footer-col, .logged-in, #user-status, #sc-info, .context-box, #user-tabs, #main-content-inner, .restrictions, .controls, .actionbar, .social-sharing, #secondary-content-inner, .track-comments, .big-action-button, .site-login-form-wrapper, #zoom-content-wrapper, .post-body, .post-wrapper, .db_border, .blocking-user, .action-buttons-inner, #header ul#main-nav li.nav a, .open-submenu, .pagination .current, .pagination a, div#page, div#footer, .auto-submit, .notification-inner, .actions, .player, .next, .stream__notice, .sound__soundActions, .listenContent__inner

{
background: black !important;
}

input, span, code, textarea, pre, div, label, select, .pretty-date

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code, .button, .link-button, .icon-button

{
background: #333333 !important;
}

}
