@-moz-document domain('plus.google.com'), url-prefix('https://plus.google.com') {
#content,
#contentPane,
#content > div {
 width: auto!important;
}
#contentPane {
 padding:0 188px 0 195px;
 display: block!important;
}
#content > div > div:first-child {
 left: 0px!important;
}
#content > div > div.a-l-k {
 position: absolute!important;
 top: 0px!important;
}
#content > div > div:last-child {
 right: 0px !important;
}
.mA {
 width:auto !important;
}
.k5,
.ph {
 padding-left: 77px !important;
}
}
