css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.tokeofthetown.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

button, #wrapper2, #wrapper3, #wrapper4, #Sidebar, #Sidebar div, #Footer, .Scribol, .Entry_Footer, .dsq-textarea-wrapper

{
background: black !important;
}

span

{
background-color: black !important;
}

input, span, code, textarea, pre, div

{
color: white !important;
}

#wrapper2, #wrapper3, #wrapper4

{
border: 8px solid black !important;
}

/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
