css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("omega-day.com") {

/* Favoritenbutton */
div.layout-menu ul.menu li.favourites {
    display:none !important;}

/* Liste breiter und h√∂her */
div.layout-menu ul.menu ul.planets li.label {
    display:none !important;}

div.layout-menu ul.menu ul.planets {
    height:auto !important;
    margin-top:15px !important;}

div.layout-menu ul.menu ul.planets li#quickjump {
    width:auto !important;
    height:auto !important;
    overflow:visible !important;
    padding:5px !important;
    border:1px solid #15607F !important;
    -moz-border-radius:4px !important;
    border-radius:4px !important;}

div.layout-menu ul.menu ul.planets li#quickjump table {
    width:100% !important;}
}
