css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ft.com") {
#top-ad {display: none !important;}

.ad-placeholder {display: none !important;}
.ad-placeholder p {display: none !important;}
.ad-placeholder p.loader {display: none !important;}

.ad {display: none !important;}

.ad-banlb {display: none !important;}

.ad-container-banlb {display: none !important;}
html>body .ad-container-banlb {display: none !important;}

.banlb-pad-top {display: none !important;}
.banlb-pad-bottom {display: none !important;}

.ad-mpusky {display: none !important;}
html>body .ad-mpusky {display: none !important;}
.ft-story-body .ad-mpusky {display: none !important;}

.ad-mpu {display: none !important;}
html>body .ad-mpu {display: none !important;}
.center .ad-mpu {display: none !important;}

.ad-container-mpu {display: none !important;}

.ad-nrwsky,
.ad-wdesky {display: none !important;}
.ft-story-body .ad-nrwsky,
.ft-story-body .ad-wdesky {display: none !important;}
.ad-nrwsky {display: none !important;}
.ad-wdesky {display: none !important;}

.ad-container-nrwsky {display: none !important;}
.ad-container-wdesky {display: none !important;}

.winger .ad-container-nrwsky {display: none !important;}
.winger .ad-container-wdesky {display: none !important;}


.ad-artbox {display: none !important;}

.ad-container-artbox {display: none !important;}


.ad-fthbox {display: none !important;}


.ad-container-fthbox {display: none !important;}


.ad-tlbx {display: none !important;}
.ad-container-tlbx {display: none !important;}


.ad-fmbut2{display: none !important;}
.ad-container-fmbut2 {display: none !important;}


.ad-lhn,.ad-mktbx{display: none !important;}
.ad-container-lhn,.ad-container-mktbx {display: none !important;}

.ad-article {display: none !important;}
#ad-placeholder-fthbox {display: none !important;}

#ad-placeholder-oob{display: none !important;}
}
