css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.jp") {

  #head
 ,.headline
 ,#side
 ,#foot
 ,div[class*="ad_"]
 ,.entrybox
 ,.pagenavi
 ,.cat
 ,.EntryMoreBanner
 ,.entry_side
 ,.entry_side+div
 ,div[class*="amazon"]
 ,.recententries
 ,.rakuten_widget
 ,#entry_actions
  { display:none !important; }

  body { background: #EFF2E1 !important; }
  #entry_detail h2 { padding: 1em 0 !important; }
  #entry_detail .entry_data { margin: 2em !important; }
}
