css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("craigslist.org")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, aside

{
color: white !important;
background: black !important;
}

select, button, fieldset, header

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code, .personals_preferences, .personals_attrbubble

{
background: #333333 !important;
}

/* cl specific */

.i, .empty
{
background: url("http://img546.imageshack.us/img546/3885/20ja.jpg") !important;
}

}
