css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.heise.de/newsticker/") {
  #container_content {
    top: 5px !important;
  }
  #sitemap, #navigation, #navi_top_container, #navi_bottom,
  #logo_bereich, #social_bookmarks, .related_items, .readspeaker {
    display: none !important;
  }
  #sitemap{
    top: 15px !important;
  }
  #breadcrumb{
    top: 0px !important;
    background: none !important;
    border: none !important;
    font-weight: bold !important;
    font-size: 120% !important;
    padding-top: 1ex !important; 
    padding-bottom: 1ex !important; 
  }
  #container_content {
    border: none !important;
  }
  #navi_bottom {
    bottom: -30px !important;
  }
  #mitte_rechts, #bannerzone, .adbottom,
  .bcadv, .adbottom_itmarkt, .news_option {
    display: none !important;
  }
  #mitte, #mitte_links, #container, #container_content {
    width: 100% !important;
    min-width: 50% !important;
  }
  h4 {
    margin-top: 2ex !important;
    padding: 2px 5px 2px 5px !important;
    background-color: #eed !important;
  }
  a {
    text-decoration: none !important;
  }
}
