css:
@-moz-document domain('ex.ua') {

body {
background: #eee;
padding: 0px 0 0 0;
}


*[style="margin-top: 24px;"] {
margin-top: 0 !important;
}

a[onClick*="social"], div[id*="plusone"], #ad_block, a[href*="ad_click"], #index_box {
display: none !important;
}

#search_box {
background: #d2dfe7 !important;
height: 50px !important;
width: 100% !important;
top: 0;
left: 0;
}

#search_help {
display: block !important;
top: 10px !important;
text-align: left !important;
left: 50%;
}

#search_line {
display: block !important;
left: 20px !important;
top: 11px !important;
}

.panel tr td  {
background: #fff;
}

div[style*="height: 28px;"] {
display: none !important;
}

input[type="text"], #search_text {
font-size: 18px;
width: 300px;
}

.small_button {
padding: 2px 20px;
font-size: 18px;
}

.button {
padding: 2px 20px;
font-size: 18px;
}

select.small {
font-size: 18px;
padding: 4px 5px;
}

#search_button {
padding: 2px 20px;
font-size: 18px;
left: 330px !important;
top: 8px !important;
}

.r_button a {
background: #fff;
font-size: 16px;
}

table {
margin: 0 auto;
}

.copyright, table.include_0 td {
background: #fff;
}

h1, h2 {
font-size: 21px;
border-bottom: 1px solid #ccc;
background: #fff;
padding: 8px 15px 10px 15px;
}

a[id*="play"] {
background: #eee;
font-size: 15px;
padding: 8px 20px;
}

}
