@-moz-document domain("reakcio.hu") {
#header{height:0px!important;margin:0!important;padding:0!important}
#search{position: absolute; right:0!important; top:-10px !important; border:none!important}
.searchlinkbox{position: absolute; right:180px!important; top:0px !important;width:100px!important}
#header h1, IFRAME {display:none!important}
}

@-moz-document domain("reakcio.blog.hu") { 
#header{display:none!important}
#leftWrapper, #leftWrapper>*, #rightWrapper, #rightWrapper>* {background-color: #EEEEEE !important} 
h3{font-size:small!important; margin: 0 !important}
IFRAME {display:none!important}
}
