css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("engadget.com") {

*{font-family:tahoma!important;}
html,body{background:#000!important;color:#fff!important;}
a{color:#0cf!important;}

/*top stuff*/
.topLeader,#slice,#GH_{background-color:#111!important;}
.engadget_hdr_hdr{background-color:#222!important;}
.main_nav_bar{border-color:#222!important;}
#mid-hero{background-color:#222!important;}
.my-little-hero,.my-little-hero div{border-color:#555!important;}

/*side stuff*/
.eng-apps,#fb_container{display:none!important;}
.col2{background:#000!important;}
.col2_most_commented,.mc_heading{background:#000!important;border-color:#222!important;}
.col2_most_commented a{color:#000!important;}

/*posts*/
.post_header{border-color:#222!important;}
.post_content div{background:transparent!important;color:#fff!important;}
}
