css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.jeuxvideo.com") {
 .bloc1,.titre_bloc,.titre_bloc span,.titre_dossier,.titre_dossier em,#nom_jeu,html,body {background:transparent !important;}
 .titre_bloc span{background:white !important;}
 .bloc1 .bloc_inner {border: 1px solid #bddfef !important;}
 .bloc1,.titre_bloc,.titre_bloc span,.titre_dossier,.titre_dossier em {color:black !important;}
 #contenu,.titre_bloc span{display:block !important;}
 #logo,#banner,#header,#menu2,#onglets_jeu,#nom_mc,#col2,#footer,#barre_outils_v2,.hautpage,.lien_base {display:none !important;}
 #col1,#contenu {float:none !important;}
 html,body,#global,#page,#contenu {margin:0 !important;}
 #contenu {margin:1cm !important;}
 #test_notes p {margin-left:100px !important;}
 #test_notes h4+p {margin-top:-50px !important;}
 html,body,#global,#page,#contenu {padding:0 !important;}
 #nom_jeu h1{position:static;}
 #nom_jeu a {right:0;}
 #nom_jeu a {top:0;}
 html,body,#global,#page,#titre_jeu,#nom_jeu {width:100% !important;}
 #col1 .fiche_tech li,#col1,#nom_jeu a,#nom_jeu h1,#nom_jeu,#contenu {width:auto !important;}
 .astuces_nav,.news_nav,.titre_bloc span{width:100% !important;}
 #test_notes p,#col1 .fiche_tech li {width: auto !important;}
 #test_notes li {width: 50% !important;}
}
