/* Color background without all of the background images but with the DIV bg-images. */



{

div 
,p,header img,header a,nav /* cnet.com */
,h1,h2,h3
,footer#footer
,a.slideHref
,body.site
 {background:none!important;} /* eliminates some background images */

/* h1,h2{padding-left:5pt!important} /* affects also search results in Cnet.com */

/* html *,* /* */
div,div *,html,body,td,span,li,p,font,blockquote,dd,h1,h2,h3,h4,h5,h6
{text-shadow:none!important;color:black!important;background-color:#C1D7F2!important;
}

a:link{color:mediumblue!important}
a:visited{color:purple!important} 
a:hover,a:active{color:#8F006A!important}

input {border-color:#2E8B57!important;} /* except the input fields */



}
