css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mygranturismo.net") {

select {
    background-color: #FFFFFF !important;
    border: solid !important;
    color: #000000 !important;
    font-weight: 100 !important;
}

.cl, .cl a:link, .cl a:visited {
    color: #ffffff !important;
    text-decoration: none;
}

.carlist_table p {
    color: #000000 !important;
}

#wrapper {
    font-size: 8pt !important;
}

#addthis, #Logosite, #lang_switch, p.pwvar, div.mod {
    display: none ! important;
}

.switch_container {
    border-left: 2px solid #777777 !important;
    border-right: 2px solid #777777 !important;
    border-top: 1px solid #777777 !important;
}

.topnav a.ui-corner-all {
    font-size: 14pt !important;
}

ul.ui-autocomplete {
    width: 800px !important;
    height: 400px !important;
}

.garage_cont .car_entry span {
    font-size: 14pt;
}

.rc1, .rc2, .bspec {
    font-size: 12pt;
}

}
