css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://utassault.net/leagues/tables.php"),
               url-prefix("http://utassault.net/clans/claninfo.php"),
               url-prefix("http://utassault.net/leagues/schedule.php"),
               url-prefix("http://utassault.net/players/playerinfo.php"),
               url-prefix("http://utassault.net/matches/matchinfo.php"),
               url-prefix("http://utassault.net/clans/") {

table { border-collapse:collapse; margin:0 auto; width:95% !important }
td { color:#ddd !important; padding:2px; border-color:#333 !important }
td[rowspan="3"] { text-align:center }
td[rowspan="3"] a { font-size:18px }
td[bgcolor="#009200"] { background-color:#060 !important }
td[bgcolor="#998200"] { background-color:#660 !important }
td[bgcolor="#861300"] { background-color:#600 !important }

font[color="#000000"] { color:#fff !important }

table[bgcolor="#222222"] { border-color:#444 !important }

}

@-moz-document url-prefix("http://utassault.net/leagues/tables.php"),
               url-prefix("http://utassault.net/clans/claninfo.php") {

table[bgcolor="#222222"] tbody tr:nth-child(2n+1) td { background-color:#181818 !important }
table[bgcolor="#222222"] table tr:nth-child(2n+1) td { background-color:transparent !important }
table[bgcolor="#222222"] tbody tr:hover td { background-color:#311 !important }

}

@-moz-document url-prefix("http://utassault.net/leagues/menu.php"),
               url-prefix("http://utassault.net/clans/admin-menu.php") {

table { border-collapse:collapse; border-color:#444 !important }
table table:nth-child(2n+2) { background-color:#0f0f0f !important }
table table { margin-bottom:2px }

}

@-moz-document url-prefix("http://utassault.net/matches/matchinfo.php") {

hr { display:none !important }

}
