css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ubuntu.com"),
domain("ubuntuforums.org"),
domain("askubuntu.com"),
domain("omgubuntu.co.uk")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h2, h3, h4, h5, h6, font, strong, form, footer, fieldset

{
color: white !important;
background: black !important;
}

code, select, button, .nav-global, #nav-global, header.banner, .homepage-features div, #main-content, .cookie-policy, .box, .footerwrap, .container, .subheader, .newsletter-anon, #topbar, #header, #hero-content, .module, #newsletter-ad, .form-item, .preferred-login, .mini-counts, .question-status, .body_wrapper, .toolsmenu, .postrow, .posthead, .userinfo_noavatar, .userinfo, .textcontrols, .blockrow, .group, .bbcode_quote, #primary, #secondary, .primary-container, .branding, #container-inner, #second-nav, #container-inner div, #footer, #container

{
background: black !important;
}

span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre

{
background: #333333 !important;
}

}
