css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("2r.ldblog.jp") {

/*他サイトの更新情報*/
.rss-headline,.rss-headline2{display:none !important;}
div.blog-title-outer-3{margin:auto !important;}

/*左の広告*/
div#sub{display:none !important;}
div#main>div.column-inner{margin: 0 206px 0 0 !important;}

/*右のサイドバー広告・他サイト更新情報*/
.side>iframe,
div#extra>div.column-inner>div.column-inner-2>div:nth-of-type(1),
div#extra>div.column-inner>div.column-inner-2>div:nth-of-type(3),
div#extra>div.column-inner>div.column-inner-2>div:nth-of-type(7),
div#extra>div.column-inner>div.column-inner-2>div:nth-of-type(9)
{display:none !important;}

/*記事内広告*/
.amazooonlnk,.kizi{display:none !important;}


}
