css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.com") {

html, body, #logo img, #logo {
  background-color: #3A3838 !important;
}

/* popup preview thing */
#xjs, .vspib { display: none !important; }


/* top bar */
.sfbgg, #appbar {
 background-color: #3A3838  !important;
 border: none !important;
}


.a, cite, cite a:link, cite a:visited, .cite, .cite:link, #mbEnd cite b, #tads cite b, #tadsb cite b, #tadsto cite b, #ans > i, .bc a:link {
  color: #4F7E82!important;
}

.s, .s em {
  color: #CDCECC!important;
}

a:link, .w, .q:active, .q:visited, .tbotu {
  color: #D2DDBC !important;
}

.mitem {
  background-color: #3A3838 !important;
}

.msel { color: white !important; }
#ms a:hover { color: white !important; background-color: #282828!important; }

#leftnav, #leftnav li, #nav {
  background-color: #3A3838 !important;
}

.tbos, .tbots, .tbotu {
  color: #7F7F7F !important;
}


/* search box */
.lst, .lst-tbb {
  background-color: #232020 !important;
  color: #FFFFFF !important;
  border: none !important;
}

/* stupid links below bottom search box */
#fll, #bfl { display: none !important; }


.msel, .lnsec, #center_col {
  border: none !important;
}

}
