css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ex.ua"), domain("www.ex.ua")
{
  div[id^="ad"],
  #search_link,
  #search_box,
  #ad_block_1,
  #ad_block_2,
  #announce,
  div[class^="ad"] {display:none !important;}
  #search_line, #search_text {font-family: Arial; font-size: 16px; height:20px; width:350px !important;}
  .button {height:30px !important; width:100px !important;}
  body, b {font-family: Arial; font-size: 16px !important;}
  a, u {text-decoration:none; font-family: Arial; font-size: 14px !important;}
  select, input {font-family: Arial; width: 150px; height: 25px; font-size: 16px !important;}
}
