css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anond.hatelabo.jp") {

   #globalheader
  ,#breadcrumbs
  ,.navigation
  ,#afc
  { display: none !important; }

  .hatena-body { width: 800px !important; }
  div.refererlist ul { background-color: #FFF9EF !important; }
  div.section h3 span.sanchor { color: #BA755B !important; }
  h2 { background-color: #E5C0B3 !important; }
  div.day p { margin-bottom: 2.0% !important; }
  div.section { margin: 2% 3% !important; }
  a:link    { color: #BA755B !important; }
  a:visited { color: purple !important; }
  a.keyword { border: none !important; color: #333333 !important; }
  div.caption {
    border-bottom: 1px solid #BA755B !important;
    margin: 0 !important;
    padding: 8px 0 0;
    background-color: #FFF9EF !important;
  }

  * {
    font-family:'ヒラギノ明朝 Pro W3','Hiragino Mincho Pro','メイリオ',Meiryo,'ＭＳ Ｐゴシック',sans-serif !important;
    text-shadow: 0px 1px 0px rgba(255,255,255,0.5) !important;
    letter-spacing: -0.03em !important;
  }
  body     { background-color: #FFF9EF !important; border: none !important; color: #333333 !important; }
  div.body { background-color: #FFFBF5 !important; border: none !important; }
  .hatena-body { margin: 2em auto !important; }
  .box-curve { box-shadow: 1px 1px 2px rgba(28,28,28,0.1) !important; }
}
