css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Keyboard shortcuts/accesskeys for web elements <http://userstyles.org/styles/56015/keyboard-shortcuts-accesskeys-for-web-elements> */

*[accesskey]:after {
	-moz-border-radius:3px!important;
	-webkit-border-radius:3px!important;
	background:#ddd!important;
	border-bottom:1px solid #999!important;
	border-left:1px solid #bbb!important;
	border-right:1px solid #999!important;
	border-top:1px solid #bbb!important;
	color:#555!important;
	content:attr(accesskey)!important;
	display:inline-block!important;
	font:9px monospace!important;
	height:13px!important;
	left:3px!important;
	line-height:13px!important;
	overflow:hidden!important;
	position:relative!important;
	text-align:center!important;
	text-transform:lowercase!important;
	top:3px!important;
	width:13px!important
}

*[accesskey]:focus:after, *[accesskey]:active:after {
	background:#ccc!important;
	border-bottom:1px solid #aaa!important;
	border-left:1px solid #888!important;
	border-right:1px solid #aaa!important;
	border-top:1px solid #888!important;
	color:#444!important
}
