css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("redstate.com") {
body {
    background-color: #333 !important;
}

html body div#container div#main div#content div.storybox div.roundedfg div.roundedBoxContent,  div.roundedBoxContent,div.comment div.title {
background-color: #E0DFDB !important;
padding:10px !important;
}
.rounded * {
    background-color: #E0DFDB !important;
}
h3.storytitle a , div.entry p, p.storydate{
font-family: "Trebuchet MS","Avenir 85 Heavy", eurostile, Arial !important;
}
h3.storytitle a {
font-size: 2em !important;
font-weight:bold !important;
}
div.entry p{
font-size: 1.3em !important;
font-family: "Trebuchet MS","Avenir 85 Heavy", eurostile, Arial !important;
margin-bottom:15px !important;
line-height:145%;
color:#555 !important;
}
html body div#container div#main div#content div.navigation p.floatleft a, html body div#container div#main div#content div.navigation p.floatleft , html body div#container div#main div#content div.navigation p.floatright a, html body div#container div#main div#content div.navigation p.floatright{
font-family: "Trebuchet MS","Avenir 85 Heavy", eurostile, Arial !important;
color: #ccc !important;
}
}
