css:
*{
font-family:"Segoe UI";
}
.convlog-msgs>div{
padding:3px;
font-family:"Segoe UI";
clear:both;
position: relative;
}

.convpanel div.convmain div.convbottom{
height:128px
}

.convlog{
bottom:128px;}

.convpanel div.convmain div.convbottom div.convinput .voiceim_toggle{
right:0px;
}

.convpanel div.convmain div.convbottom div.convinput div.convinput-text-container{
right:0px
}
.convpanel div.convmain div.convbottom div.convinput div.convinput-text-container textarea{
height:117px!important;
width:100%;
font-family:"Segoe UI";
background:#fff;
font-size:13px;
}

.convpanel div.convmain div.convbottom div.convtyping{
bottom:6px;
opacity:0.6;
right:75px;
left:auto;
}
.convlog .na{
display:block;
position:absolute;
left:60px;
font-family:"Segoe UI";
font-size:10px;
font-weight:bold;
color:#666;
}
.convlog .ts{
display:block;
position:absolute;
font-family:arial;
font-size:10px;
}

.convlog .ms{
display:block;
padding-left:200px;
}



.convlog-msgs{
background:#F0F7FC;
}

.convlog .convlogitem_bk0_old {
background-color: #F0F7FC;
}
