css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

tab[selected="true"] {
 min-width: 230px !important; max-width: 300px !important;
 }

tab:hover {
 min-width: 250px !important; max-width: 300px !important;
 }

tab[selected="false"] {
 min-width: 50px !important; max-width: 50px !important;
 }


#addon-bar:not(:hover) {margin-bottom:-20px;}
#addon-bar:not(hover) {min-height: 24px !important;   -moz-transition: all 0.2s ease;
}
#addon-bar {position:absolute; box-shadow: 0 0 3px #999999;}
#browser-bottombox > * {position:static}

#addonbar-closebutton {
    display: none !important;
}

.tabs-closebutton-box, .tab-close-button {display: -moz-box !important;}
