css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.knuddels.de") 

{

/* Links */
a {color: #0C72A5 !important; text-decoration:none !important}
a:hover {color: #2086B9 !important;}
.bbutton a {color: #F2F2F2 !important;
text-shadow: #0C72A5 0px 0px 15px !important}
.bbutton .btmi {color: #F2F2F2 !important;
text-shadow: #0C72A5 0px 0px 15px !important}
#knavi .navi ul li .menua {color: #F2F2F2 !important;}
#knavi .navi ul li .menua:hover {color: #2086B9 !important;}

/* Facebookbutton */
#fb_link, #facebook {display:none!important;}

/* Uservorschau */
#online_user .userimage{border:1px solid #000!important;
padding: 0px !important;}
#online_user{background-image:
url(http://i55.tinypic.com/zwzxuh.jpg) !important;
background: transparent !important;
top:-50px!important;}

/* Hintergrund */
html, body {background:
url(http://i55.tinypic.com/zwzxuh.jpg) right fixed !important;}

/*Kategorien */
.category_name {color:#F2F2F2 !important;}
.categories .category_bubble {background-color: transparent !important;}

 /* Divfixes*/
#kft .ftbox .l {width:29px;background: transparent  !important;}
#kft .ftbox .r {width:29px;background: transparent !important;
position:absolute; right:0;}
#kft .ftbox div.content{background:transparent !important;}

/*Navigation*/
#knavi {opacity: 0.5; -moz-transition:opacity .3s ease-in;}
#knavi a {background:none !important; border:none!important;}
#knavi:hover {opacity:1.0 !important}
#knavi .navi ul li .menua .border, #knavi .navi ul li .menub .border
{display:none !important;}
#knavi .navi li.first div.border {display:none !important;}
#knavi .border-top {background-color: #0C72A5 !important;
border-bottom: transparent !important}
#knavi .border-top .border {background: transparent !important}

/* Weitere Anpassungen */ 
#bd3 {background: transparent !important;}
#khd div.content {background: transparent !important;}
#khd .r {background: transparent !important;}
#khd .l {background: transparent !important;}

/* Schriften */
h2 {color: #0C72A5 !important; text-shadow: #000 0px 0px 1px}
h1 {color: #0C72A5 !important; text-shadow: #000 0px 0px 1px}
.justified {color: #000 !important}
.date {color: #000 !important}
.author {color: #000 !important}
.mi {color: #000 !important}
.index .pwforgotten {color: #000 !important}
#form-reg .label, #form-reg .formlabel {color: #000 !important}
#form-reg .note, .note {color: #000 !important}
#kmain .blogentry .author .name {color: #0C72A5 !important;}
#khd ul.hdm a {color:#F2F2F2 !important;
border-bottom: 3px solid #0C72A5 !important}
#khd ul.hdm a:hover {color:#0C72A5 !important;
border-bottom: 3px solid #F2F2F2 !important}

}
