@-moz-document domain("www.tvtv.de") {
#main_left {
top: 0px !important;
bottom: 0px !important;
}
#main_right {
top: 0px !important;
bottom: 0px !important;
}
#banner-top, #details_header, #details_right, #banner-right, .linkbutton, .list_detail, .note, .FBConnectButton, .FBConnectButton_Small, .FBConnectButton_Text {
