css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mangareader.net") {

iframe, object, embed {
    opacity: .1 !important;
    height: 1px !important;
    overflow: hidden !important;
}
#imgholder {
    top: 0;
    width: 100% !important;
    margin: 0 !important;
    padding: 0 !important;
    background: #fff !important;
    display: block !important;
    position: absolute !important;
}
#imgholder * {
    display: block;
    margin: 0 auto;
}
#topchapter {
    background: #000;
    position: fixed;
    right: 1em;
    top: -2em;
    z-index: 2;
}
#topchapter,
#topchapter * {
    color: rgba(0,0,0,0) !important;
    background: none !important;
}
#selectmanga {
    display: none;
}
#navi * {
    color: #000 !important;
}
#navi .prevnext a {
    border: 1px solid #ccc !important;
    padding: 2em 1em !important;
}

}
