css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rt.com") {

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, footer, div.oh, div.videobox, .short.yellow .content, .content, .content-tagcloud, .bot-tall-grey, div.main, div.header_left, div.blockr, div.footer-cont, .story .general, div.c, div.shadow, .topnews2 .label, div.secttop2, .twtr-hd, div.twtr-tweet-wrap

{
color: white !important;
background: black !important;
}

/*links*/

a, a.calendar_ref span, .blockr_top10 a span, .secttop

{
color: #0040ff !important;
}

.comments, .tags, a.calendar_ref span, div.marquee, .marquee .i, .secttop, .main-footer, .main-header, .b-main_extension

{
background: black !important;
}

/*highlight*/
textarea, pre, .menuhor ul li.tosecondmenu ul li:hover a

{
background: #333333 !important;
}

}
