css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thesun.co.uk"),
domain("thesunmagazine.org") {

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, input, footer, div#header_mid, div#header_bottom, #content-516, div#container, #content .section_head, div.subscription, div.subscribe, #column_a .callout, #column_a .callout .bg, #main_nav, #column_b .callout, #column_b .callout .bg, #column_b .callout.newsletter .bg h1 span, #column2-index, #col2-index-com, .bg-fff, div#ngn-footer-hygiene, div#ngn-footer, div#active-search, #column4-index, .rightColumn-custom-html, div#column2, div#content-682, div#column3-280, div.chicklet-block, div#social-top, #bodyText div, div.border, div#LeftNavigation ul li.icon-partner a .icon-partner-title, .ngn-playlist-items, div.ngn-playlist-wrapper, .article-v2 #column4, .article-v2 .related-stories, #article-v2-wrapper

{
color: white !important;
background: black !important;
}

/*links*/

a, #LeftNavigation ul.white-border-2 li a, .ngn-playlist-items span.para, #ngn-catagories span.para, .ngn-playlist-items strong, #ngn-catagories strong, a.puff

{
color: #0040ff !important;
}

#LeftNavigation, #LeftNavigation ul.white-border-2 li a, div.dropmenuitem, a.puff

{
background: black !important;
border: black !important;
}

/*highlight*/

textarea, pre

{
background: #333333 !important;
}

}
