css:
@-moz-document domain("addons.mozilla.org") {
    
    .primary div {
        opacity: 1 !important
    }
    
    .notavail {
        display: none !important
    }
}

@-moz-document url("about:addons") {

    .addon.addon-view,
    #detail-view {
        background: none !important
    }
    
    .global-warning-container,
    .global-warning,
    .warning,
    #userstyle-footer {
        display: none !important
    }
}
