css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newtorrents.info") {

div.last_posts > center:first-child > a:first-child > img, div#last_posts > center > span + a > img,
#main > div > center > table > tbody > tr > td:first-child,
#main > div:last-child > a, #promo, #top, 
#adbrite, #yourad, #topbanner {display: none !important}

div#files {
    background-color: #cccccc !important;
    border: 1px solid #000000 !important;
    }

div#last_posts > center > span {color: #dce9f6 !important;}

div#news_left::before {
    content: url("http://www.newtorrents.info/img/nt_3.jpg") !important;
    display:block!important;
    text-align:center!important;
    }

div#news_left {
    width:100%!important;
    border:0px!important;
    }

div#nt_search {
    height: auto !important;
    max-height: 75px !important;
    }

}
