css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://imdb.com/board),
url-prefix(http://www.imdb.com/board) {
#root > H3,
P:not(:last-child)
{display:none !important;}
}
@-moz-document url-prefix(http://imdb.com),
url-prefix(http://www.imdb.com) {
#root > H3,
#root > P,
#root > LAYER:first-child + DIV + A + TABLE + A + H3 + P,
#root > LAYER:first-child + DIV + A + TABLE + A + H3 + P + P
{display:none !important;}
}
