/* ==UserStyle==
@name           Amazon Fewer Distractions
@namespace      amazontidy
@version        1.0.0
@description    This style fades out irrelevant sections of Amazon search and product pages until hovered, giving more prominence to the main content.
@author         BoffinbraiN
==/UserStyle== */

@-moz-document url-prefix(http://www.amazon.), url-prefix(https://www.amazon.), url-prefix(http://amazon.), url-prefix(https://amazon.) {
	/* Hide ads */
	#cbcc_banner,
	#nav-upnav {
		display: none;
	}

	/* Fade out sections unrelated to the current product, until hovered */
	#vtpsims:not(:hover),
	#rhf:not(:hover),
	#purchaseShvl:not(:hover),
	#sims_fbt:not(:hover),
	#purchase-sims-feature:not(:hover),
	#fallbacksessionShvl:not(:hover),
	#sp_detail:not(:hover),
	#sp_detail2:not(:hover) {
		opacity: 0.3;
	}
}
