css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pastebin.com"),
domain("pastebin.ca"),
domain("paste2.org"),
domain("bpaste.net"),
domain("dpaste.com"),
domain("dpaste.de"),
domain("nopaste.info"),
domain("imagebin.org"),
domain("sprunge.us"),
domain("pastie.org"),
domain("privatepaste.com"),
domain("paste8.com"),
domain("pasteall.org"),
domain("wklej.org")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, ol, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, pre

{
color: white !important;
background: black !important;
}

select, fieldset, #header_middle, .button1, #code_frame div, #container, #secondary, #header

{
background: black !important;
}

div, span, a, input

{
background-color: black !important;
}

select, input, span, code, textarea, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, code, button

{
background: #333333 !important;
}

#monster_frame
{
box-shadow: 0 0 10px 5px #000000 !important;
}

}
