css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://blog.livedoor.jp/no1_only1/archives/") {

p[style="text-align: center;"],
div[style="overflow: auto;font-size:12px; width:728px; height:515px;"],
div#contentin>*:not(#articlebody),
div#leftbody,
div#rightbody>div:nth-of-type(1),
div#rightbody>div:nth-of-type(2),
div#rightbody>div:nth-of-type(3),
div#rightbody>div:nth-of-type(6),
div#rightbody>div:nth-of-type(7),
div#rightbody>div:nth-of-type(8),
div#rightbody>div:nth-of-type(9),
div#rightbody>div:nth-of-type(11),
div#rightbody>div:nth-of-type(12),
div#rightbody>div:nth-of-type(14){
    display:none;
}

div#content{
    width:auto !important;
    float:none !important;
}

div#articlebody,div.datebody{
    background:#ffffff !important;
}

a[name="amazletlink"],
#a8_rbr,
b>iframe,
.amazlet-box,
.mainmore>iframe{
    display:none !important;
}

}
