css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("disqus.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, fieldset, #global-nav, #local-nav, #page-header, #page-header .container, #feature-explorer, div#network-footer, .dsq-popup .dsq-popup-wrapper, .dsq-popup .dsq-button, .message, #content, #dashboard .secondary, #dashboard, #local-nav ul li a, #local-nav ul li span.username, #profile-content, #embed-footer, #toolbar, #facebox .body, div.box, div.alert, div.tabs, #network-stats, #explore, #active-conversations .most-popular-comment

{
color: white !important;
background: black !important;
}

select, button, #nav li.selected a, .tabs li a, .more-btn

{
background: black !important;
}


div, span, a

{
background-color: black !important;
}


select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}

/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code, .tabs li.selected a

{
background: #333333 !important;
}

}
