css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://tieba.baidu.com") {
    body {
    	font-family:Microsoft YaHei !important;
    }
    a, a> font {
    	text-decoration:none !important;
    	color:rgb(28, 64, 120) !important;
    }
    a:visited {
    	color:rgb(130, 130, 130) !important;
    }
    #thread_list th, .l_menu {
    	background-color:rgb(194, 207, 239) !important;
    }
    img#tieba_logo, table#rightAd, div.l_banner, #frs_banner_ad{
    	display:none !important;
    }
    .p_thread,.l_container>.l_core>.p_postlist, .l_thread {
    	width:99% !important;
    }
    .thread_list tr {
    	height:35px !important;
    }
    .goTop_wide {
        left:98% !important;
    }
}
