css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/* Facebook - Photograph Style Pictures */
/* Created by JohnTuley */
/* http://userstyles.org/users/69668 */

.uiPhotoThumb, .uiScaledThumb > .uiScaledImageContainer {

    box-shadow: 1px 1px 5px #121212 !important;
    border: 5px solid #ffffff !important;
    margin: 10px !important;
    -moz-transform:rotate(-2deg);
    -webkit-transform:rotate(-2deg);
    -o-transform:rotate(-2deg);
    -ms-transform:rotate(-2deg);

}

.uiScaledThumb > .uiScaledImageContainer {

    max-width: 325px !important;
    margin: 10px 20px 30px 30px !important;

}

}
