css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linkedin.com"),
domain("lnkd.in"),
domain("lidcn.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, footer, fieldset, dt, dd

{
color: white !important;
background: black !important;
}

select, button, #slick-sharing-cont, #top-header, .responsive-nav, #wrapper, #text-ad-container, .header-nav, #main, .text-ad-container, #contact-list-container, #content, .profile-header, .jellybean, #premium-image

{
background: black !important;
}

div, a, .endorse-item-name

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, code, input, .bar-graph

{
background: #333333 !important;
}

}
