css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nsearch.com") {

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, footer, div.errordesc, div#xg_body, .xg_module, .xg_module_body, #main, .main-2column, .main-2column-l, .main-2column-r, #sidebar, #custom-doc

{
color: white !important;
background: black !important;
}

.xg_dock, .xg_dock a

{
background: black !important;
}

/*links*/

a

{
color: #0040ff !important;
}

/*highlight*/

textarea, pre, #xg_navigation ul li a:hover, #xg_navigation ul li a:visited

{
background: #333333 !important;
}

}
