css:
    @namespace url(http://www.w3.org/1999/xhtml);

    @-moz-document domain('taobao.com'), domain('tmall.com'){

    #site-nav .menu-item     .menu-hd {width: auto  !important;}

    /*修正FX设最小字体为13px时导航菜单的变形(width:字数*13px) */    
    #site-nav .menu-item     .menu-bd {width: 87px  !important;}
    #site-nav .mytaobao      .menu-bd {width: 100px !important;}
    #site-nav .seller-center .menu-bd {width: 100px !important;}
    #site-nav .favorite      .menu-bd {width: 87px  !important;}
    #site-nav .services      .menu-bd {width: 217px !important;}

    /*购物车*/
    #site-nav .cart a {width: auto !important;}
    .mini-cart-bt   a {width:113px !important;}
    
    /*高级搜索*/
    .top-header .tsearch-action {width: 52px !important;}
    
    }
